package leapmotion.main;

public class LoopWait {
	
	// Classe servant à réduire le lag dans les boucles infinies
	
	public void w(){
		try {
			Thread.sleep(1);
		} catch (InterruptedException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
	}
     
	 
	 
	 
	 
	 
	 
	 
	 
	 
	 
	 
	 
}